#define PIN_G 9
#define PIN_B 10
#define PIN_R 11

void setup() {
    Serial.begin(9600);
}

void loop() {
    for (int i = 255; i > 0; i--) {
        int blue = 128 -i;
        //twos_complement(blue);

        analogWrite(PIN_R, i);
        analogWrite(PIN_B, blue); // 127 -> 0 -> 127 
        analogWrite(PIN_G, 255 - i);

        delay(50);
    }

    for (int i = 0; i < 255; i++) {
        int blue = 128 -i;
        //twos_complement(blue);

        analogWrite(PIN_R, i); 
        analogWrite(PIN_B, blue); // 128 -> 0 -> 126 
        analogWrite(PIN_G, 255 - i);

        delay(50);
    }
}

void twos_complement(int binary) {
    if (binary < 0) Serial.println((~binary) + 1);
    else Serial.println(binary);
}
