      else{ light_down(); }
    }
    digitalWrite(PIN_STORE, HIGH);
    delay(200);
  }
  // right --> left
  for(int i=0; i<8; i++){
    digitalWrite(PIN_STORE, LOW);
    for(int j=0; j<8; j++){
      if(j == i){ light_up(); }
      else{ light_down(); }
    }
    digitalWrite(PIN_STORE, HIGH);
    delay(200);
  }
}

void light_up() {
  digitalWrite(PIN_SHIFT, LOW);
  digitalWrite(PIN_DATA, 1);
  digitalWrite(PIN_SHIFT, HIGH);
}

void light_down() {
  digitalWrite(PIN_SHIFT, LOW);
  digitalWrite(PIN_DATA, 0);
  digitalWrite(PIN_SHIFT, HIGH);
}
